targets.query:
// REPLACE: your Flux query here
// Must output: _time, _value (state name), _field (series label)
//
// Example: mode segments from mission data
from(bucket: "vessel-data")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "mission_segments")
  |> filter(fn: (r) => r._field == "duration_s")
  |> filter(fn: (r) => r.mission == "${mission}")
  |> keep(columns: ["_time", "mode"])
  |> map(fn: (r) => ({_time: r._time, _value: r.mode, _field: "mode"}))
  |> group(columns: ["_field"])
  |> sort(columns: ["_time"])
