targets.query:
// REPLACE: your Flux query here
// Must output: _time, _value (numeric), _field (series label)
//
// Example: battery percentage over time
from(bucket: "vessel-data")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "battery_telemetry")
  |> filter(fn: (r) => r._field == "charge_percentage")
  |> filter(fn: (r) => r.mission == "${mission}")
  |> group()
  |> sort(columns: ["_time"])
  |> aggregateWindow(every: 5m, fn: mean, createEmpty: false)
  |> map(fn: (r) => ({r with _value: r._value * 100.0, _field: "Battery %"}))
